    public static void maximalRectangleTest(String input) {
        char[][] matrix = StringToArrayUtil.twoDimensionCharArr(input);
        for (int i = 0; i < matrix.length; i++) {
            System.out.println(Arrays.toString(matrix[i]));
        }
        System.out.println(new MaximalRectangle().maximalRectangle(matrix));
    }

    private static int MAX_VALUE = 0;
    private static final char ONE = '1';

    public int maximalRectangle(char[][] matrix) {
        MAX_VALUE = 0;
        if (matrix == null || matrix.length == 0) {
            return 0;
        }
        return maximalRectangle(matrix, 0, 0, matrix.length - 1, matrix[0].length - 1);
    }

    /**
     * 计算(x1,y1)，(x2,y2)之间的矩形面积。优化为从中间位置开始找空格，规模缩减更快。
     *
     * @param matrix
     * @param x1     (x1,y1)左上点，包含
     * @param y1
     * @param x2     (x2,y2)右下点，包含
     * @param y2
     * @return
     */
    public int maximalRectangle(char[][] matrix, int x1, int y1, int x2, int y2) {
        if (x1 > x2 || y1 > y2) {
            return 0;
        }
        if ((x1 == x2) && (y1 == y2)) {
            return matrix[x1][y1] == ONE ? 1 : 0;
        }

        if ((x2 - x1 + 1) * (y2 - y1 + 1) <= MAX_VALUE) {
            return 0;
        }

        for (int i = 0; i < x2 - x1 + 1; i++) {//总共有(x2-x1+1)行
            int currentX = ((x2 + x1) / 2) + (i + 1) / 2 * (i % 2 == 0 ? -1 : 1);
            for (int j = 0; j < y2 - y1 + 1; j++) {//总共有(y2 - y1 + 1)列
                int currentY = ((y2 + y1) / 2) + (j + 1) / 2 * (j % 2 == 0 ? -1 : 1);

                if (matrix[currentX][currentY] != ONE) {//找到第一个不为1的

                    int upSide = maximalRectangle(matrix, x1, y1, currentX - 1, y2);
                    int downSide = maximalRectangle(matrix, currentX + 1, y1, x2, y2);
                    int leftSide = maximalRectangle(matrix, x1, y1, x2, currentY - 1);
                    int rightSide = maximalRectangle(matrix, x1, currentY + 1, x2, y2);

                    int tmpMaxValue = Math.max(Math.max(upSide, downSide), Math.max(leftSide, rightSide));
                    MAX_VALUE = tmpMaxValue > MAX_VALUE ? tmpMaxValue : MAX_VALUE;
                    return tmpMaxValue;
                }
            }
        }

        //说明全部都是1
        int tmpValue = (x2 - x1 + 1) * (y2 - y1 + 1);
        MAX_VALUE = tmpValue > MAX_VALUE ? tmpValue : MAX_VALUE;
        return tmpValue;
    }

    /**
     * 计算(x1,y1)，(x2,y2)之间的矩形面积.这个虽然没错，但是执行超时。从第一行开始找，规模缩减太慢。
     *
     * @param matrix
     * @param x1     (x1,y1)左上点，包含
     * @param y1
     * @param x2     (x2,y2)右下点，包含
     * @param y2
     * @return
     */
    public int maximalRectangle2(char[][] matrix, int x1, int y1, int x2, int y2) {
        if (x1 > x2 || y1 > y2) {
            return 0;
        }
        if ((x1 == x2) && (y1 == y2)) {
            return matrix[x1][y1] == ONE ? 1 : 0;
        }

        if ((x2 - x1 + 1) * (y2 - y1 + 1) <= MAX_VALUE) {
            return 0;
        }

        for (int i = x1; i <= x2; i++) {
            for (int j = y1; j <= y2; j++) {
                if (matrix[i][j] != ONE) {//找到第一个不为1的

                    //int upSide = maximalRectangle(matrix, x1, y1, i - 1, y2);
                    int upSide = (i - 1 - x1 + 1) * (y2 - y1 + 1);
                    MAX_VALUE = upSide > MAX_VALUE ? upSide : MAX_VALUE;

                    int downSide = maximalRectangle2(matrix, i + 1, y1, x2, y2);
                    int leftSide = maximalRectangle2(matrix, x1, y1, x2, j - 1);
                    int rightSide = maximalRectangle2(matrix, x1, j + 1, x2, y2);

                    int tmpMaxValue = Math.max(Math.max(upSide, downSide), Math.max(leftSide, rightSide));
                    MAX_VALUE = tmpMaxValue > MAX_VALUE ? tmpMaxValue : MAX_VALUE;
                    //System.out.println("(i,j):" + i + "," + j + " upSize:" + upSide + " downSide:" + downSide + " leftSide:" + leftSide + " rightSide:" + rightSide);
                    return tmpMaxValue;
                }
            }
        }

        //说明全部都是1
        int tmpValue = (x2 - x1 + 1) * (y2 - y1 + 1);
        MAX_VALUE = tmpValue > MAX_VALUE ? tmpValue : MAX_VALUE;
        return tmpValue;
    }

